mapper.select:
/* 사용자PC 중복체크 selectDuplicateCheck */

select 
	case when sum(dup_pc_ip_cnt)>0 then 'DUP_PHONE_IP'
		else 'OK' end as result_code
from 
(
	select
		count(*) as dup_pc_ip_cnt
	from crec_pcip_info WITH(NOLOCK)
	where pc_ip = #{pc_ip}
) U

/* 채널 목록 selectList */ select pc_ip ,pc_ip as ori_pc_ip ,use_yn ,bigo from crec_pcip_info where 1=1
and pc_ip like '%'+ #{pc_ip} + '%'
and use_yn = #{use_yn}
and bigo like '%'+ #{bigo} + '%'
order by pc_ip asc

mapper.insert:
/* 사용자PC IP 등록 insertPcip */
insert into crec_pcip_info
(
	pc_ip,use_yn,bigo
) 
values 
(
	#{pc_ip},#{use_yn},#{bigo}
)

mapper.update:
/* 사용자PC IP updatePcip */
update crec_pcip_info

	pc_ip = #{pc_ip}
	,use_yn = #{use_yn}
	,bigo = #{bigo}

where pc_ip = #{ori_pc_ip}

mapper.delete:
/* 사용자PC IP 삭제 deleteChannel */
delete from crec_pcip_info
where pc_ip = #{pc_ip}
